insert into films (name, description, year, genre_id, minimal_age, duration_in_minutes, file_id)
values ('Аватар: Путь воды', 'После принятия образа аватара солдат Джейк Салли становится предводителем народа на''ви и берет на себя миссию по защите новых друзей от корыстных бизнесменов с Земли. Теперь ему есть за кого бороться — с Джейком его прекрасная возлюбленная Нейтири. Когда на Пандору возвращаются до зубов вооруженные земляне, Джейк готов дать им отпор.', 2022, 1, 13, 192, 1);

insert into films (name, description, year, genre_id, minimal_age, duration_in_minutes, file_id)
values ('Черный Адам', '5000 лет назад Чёрный Адам был наделен силами египетских богов и заключён в тюрьму. Теперь он освобождается из своей усыпальницы и готовится явить миру уникальный подход к борьбе за справедливость.', 2022, 7, 13, 125, 2);

